package com.example.amangeldi_abdibaitov_mo3_hw_8

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import com.example.amangeldi_abdibaitov_mo3_hw_8.databinding.FragmentMainBinding


class MainFragment : Fragment() {

    private lateinit var binding: FragmentMainBinding
    private val characterList = arrayListOf(
        ModelRv(
            "data:image/jpeg;base64,",
            "Squanchy",
            "Alive"
        ),
        ModelRv(
            "data:image/jpeg;base64,",
            "Ричард Дэниел Санчес",
            "Immortal Object"
        ),
        ModelRv(
            "data:image/jpeg;base64,",
            "Мортимер Смит",
            "Alive"
        ),
        ModelRv("", "", ""),
        ModelRv("", "", "")
    )

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View {
        // Inflate the layout for this fragment
        binding = FragmentMainBinding.inflate(layoutInflater)
        return binding.root

    }


    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initAdapter()
    }

    private fun initAdapter() {
        val adapter = CartoonAdapter(characterList, this::onClick)
        binding.rvCharacters.adapter = adapter
    }

    private fun onClick(position: Int) {
        findNavController().navigate(R.id.nextFragment, bundleOf("2" to characterList[position]))
    }

}